import React, { useState } from "react";
import axios from 'axios';

export default function Login({ onLogin }){
  const [email,setEmail] = useState("");
  const [senha,setSenha] = useState("");

  async function submit(e){
    e.preventDefault();
    const form = new URLSearchParams();
    form.append("username", email);
    form.append("password", senha);
    try {
      const { data } = await axios.post("http://127.0.0.1:8000/auth/token", form, { headers: { "Content-Type": "application/x-www-form-urlencoded" }});
      localStorage.setItem("token", data.access_token);
      onLogin();
    } catch (err) {
      alert("Erro no login. Cadastre-se primeiro em /auth/register pelo backend.");
    }
  }

  return (
    <div style={{maxWidth:480, margin:'40px auto', padding:20, border:'1px solid #ddd'}}>
      <h2>Entrar</h2>
      <form onSubmit={submit}>
        <input placeholder="email" value={email} onChange={e=>setEmail(e.target.value)} style={{width:'100%',padding:8,marginBottom:8}} />
        <input type="password" placeholder="senha" value={senha} onChange={e=>setSenha(e.target.value)} style={{width:'100%',padding:8,marginBottom:8}} />
        <button type="submit">Entrar</button>
      </form>
      <p style={{marginTop:12}}>Para testar, registre um usu√°rio via backend: POST /auth/register</p>
    </div>
  )
}
