import React, { useState } from "react";
import axios from 'axios';

export default function TransacaoForm({ onSaved }){
  const [tipo, setTipo] = useState("gasto");
  const [valor, setValor] = useState("");
  const [categoria, setCategoria] = useState("");
  const [descricao, setDescricao] = useState("");
  const [data, setData] = useState(new Date().toISOString().slice(0,10));

  async function submit(e){
    e.preventDefault();
    const token = localStorage.getItem("token");
    try {
      await axios.post("http://127.0.0.1:8000/transacoes/", { tipo, valor: parseFloat(valor), categoria, descricao, data }, { headers: { Authorization: `Bearer ${token}` }});
      setValor(''); setCategoria(''); setDescricao('');
      onSaved?.();
    } catch (err) {
      alert("Erro ao salvar transacao");
    }
  }

  return (
    <form onSubmit={submit} style={{display:'grid', gridTemplateColumns:'1fr 1fr 1fr 1fr', gap:8}}>
      <select value={tipo} onChange={e=>setTipo(e.target.value)} style={{padding:8}}>
        <option value="receita">Receita</option>
        <option value="gasto">Gasto</option>
        <option value="divida">Dívida</option>
      </select>
      <input value={valor} onChange={e=>setValor(e.target.value)} placeholder="valor" style={{padding:8}} />
      <input value={categoria} onChange={e=>setCategoria(e.target.value)} placeholder="categoria" style={{padding:8}} />
      <input value={descricao} onChange={e=>setDescricao(e.target.value)} placeholder="descrição" style={{padding:8}} />
      <input type="date" value={data} onChange={e=>setData(e.target.value)} style={{padding:8}} />
      <button type="submit" style={{padding:8}}>Salvar</button>
    </form>
  );
}
