import React, { useEffect, useState } from "react";
import axios from 'axios';
import TransacaoForm from './TransacaoForm';

export default function Dashboard({ onLogout }){
  const [resumo, setResumo] = useState(null);

  async function loadResumo(){
    const token = localStorage.getItem("token");
    try {
      const { data } = await axios.get("http://127.0.0.1:8000/dashboard/resumo", { headers: { Authorization: `Bearer ${token}` }});
      setResumo(data);
    } catch (err) {
      alert("Erro ao carregar resumo");
    }
  }

  useEffect(()=>{ loadResumo() },[]);

  if(!resumo) return <div>Carregando...</div>;
  return (
    <div style={{maxWidth:900, margin:'20px auto'}}>
      <div style={{display:'flex', justifyContent:'space-between', alignItems:'center'}}>
        <h1>Dashboard</h1>
        <div>
          <button onClick={onLogout}>Sair</button>
        </div>
      </div>
      <div style={{display:'grid', gridTemplateColumns:'repeat(3,1fr)', gap:12}}>
        <div style={{padding:12, border:'1px solid #ddd'}}><h3>Total Receitas</h3><div>R$ {resumo.total_receitas.toFixed(2)}</div></div>
        <div style={{padding:12, border:'1px solid #ddd'}}><h3>Total Gastos</h3><div>R$ {resumo.total_gastos.toFixed(2)}</div></div>
        <div style={{padding:12, border:'1px solid #ddd'}}><h3>Total Dívidas</h3><div>R$ {resumo.total_dividas.toFixed(2)}</div></div>
        <div style={{padding:12, border:'1px solid #ddd'}}><h3>Faturas não pagas</h3><div>R$ {resumo.total_faturas_nao_pagas.toFixed(2)}</div></div>
        <div style={{padding:12, border:'1px solid #ddd'}}><h3>Saldo guardado</h3><div>R$ {resumo.saldo_guardado.toFixed(2)}</div></div>
        <div style={{padding:12, border:'1px solid #ddd'}}><h3>Saldo real</h3><div style={{color: resumo.saldo_real < 0 ? 'red':'green'}}>R$ {resumo.saldo_real.toFixed(2)}</div></div>
      </div>

      <div style={{marginTop:20, padding:12, border:'1px solid #ddd'}}>
        <h3>Adicionar transação</h3>
        <TransacaoForm onSaved={loadResumo} />
      </div>
      {resumo.aviso && <div style={{marginTop:12, color:'orange'}}>{resumo.aviso}</div>}
    </div>
  )
}
